import * as React from 'react';
import { styled } from '@mui/material/styles';
import Grid from '@mui/material/Grid';
import Container from '@mui/material/Container';
import Paper from '@mui/material/Paper';
import Typography from '@mui/material/Typography';

const config = require('../config.json');

export default function RecipePage({recipeId}) {

  const [expanded, setExpanded] = React.useState(false);
  const [recipeData, setRecipeData] = React.useState({});

  React.useEffect(() => {
  fetch(`http://${config.server_host}:${config.server_port}/recipe/${recipeId}`)
    .then(res => res.json())
    .then(resJson => setRecipeData(resJson))
}, [recipeId]);

  return (
      <Paper
        sx={{
          p: 2,
          height: 500,
          }}
        >
        <Typography component="p" variant="h5">
        {recipeData.name}
        </Typography>

        <Typography variant="subtitle1">
        {recipeData.description}
        </Typography>

        <Typography variant="subtitle2">
        {recipeData.minute} minutes,
        {recipeData.n_ingredients} ingredients,
        {recipeData.n_steps} steps,
        </Typography>
      </Paper>
  );
}
