import * as React from 'react';
import { styled } from '@mui/material/styles';
import Grid from '@mui/material/Grid';
import Container from '@mui/material/Container';
import Paper from '@mui/material/Paper';
import Typography from '@mui/material/Typography';

const config = require('../config.json');

export default function RecipePage({recipeId}) {

  const [expanded, setExpanded] = React.useState(false);
  const [recipeData, setRecipeData] = React.useState({});

  React.useEffect(() => {
  fetch(`http://${config.server_host}:${config.server_port}/recipe/${recipeId}`)
    .then(res => res.json())
    .then(resJson => setRecipeData(resJson))
}, [recipeId]);

  return (
      <Grid container spacing={2}>

        <Grid item xs={8}>
          <Paper sx={{ p: 2,}}>

            <Typography variant="h5" align="left">
            {recipeData.name}
            </Typography>

            <Typography variant="subtitle1" align="left">
            {recipeData.description}
            </Typography>

          </Paper>
        </Grid>


        <Grid item xs={4}>
          <Paper sx={{ p: 2,}}>

            <Typography variant="subtitle2" align="left">
            {recipeData.minute} minutes,
            {recipeData.n_ingredients} ingredients,
            {recipeData.n_steps} steps,
            </Typography> 

          </Paper>
        </Grid>

      </Grid>
  );
}
