import * as React from 'react';
import CssBaseline from '@mui/material/CssBaseline';
import Grid from '@mui/material/Grid';
import Container from '@mui/material/Container';
import Paper from '@mui/material/Paper';
import GitHubIcon from '@mui/icons-material/GitHub';
import { createTheme, ThemeProvider } from '@mui/material/styles';

import Header from '../components/Header';
import RandomRecipe from '../components/RandomRecipe';
import RecipeTable from '../components/RecipeTable';

const theme = createTheme();

export default function HomePage() {

  const [randomRecipe, setRandomRecipe] = React.useState({});

  React.useEffect(() => {
  // Fetch request to get the song of the day. Fetch runs asynchronously.
  // The .then() method is called when the fetch request is complete
  // and proceeds to convert the result to a JSON which is finally placed in state.
  fetch(`http://${config.server_host}:${config.server_port}/random_recipe`)
    .then(res => res.json())
    .then(resJson => setRandomRecipe(resJson));
  }, []);

  const randomRoute = `/recipe/${randomRecipe.id}`

  return (
    <ThemeProvider theme={theme}>
      <CssBaseline />
          <Container maxWidth="lg">

            <Header/>

            {/* Random Recipe */}
            <Grid container spacing={2}>

              <Grid item xs={12}>
                <Paper sx={{p: 2,}}>

                  <Typography
                    variant="h4"
                    color="primary"
                    sx={{fontWeight: 700}}
                    gutterBottom>
                    Recipe of The Day
                  </Typography>

                  <Typography component="p" variant="h5" gutterBottom>
                  {randomRecipe.name}
                  </Typography>

                  <Typography variant="body1" gutterBottom>
                  {randomRecipe.description}
                  </Typography>

                  <Typography variant="body1" gutterBottom>
                  {randomRecipe.minute} minutes,
                  {randomRecipe.n_ingredients} ingredients,
                  {randomRecipe.n_steps} steps,
                  </Typography>

                  <Button variant="contained" component={Link} to={route}>
                    Make this!
                  </Button>
                </Paper>
              </Grid>

            </Grid>
            <RecipeTable/>
          </Container>
    </ThemeProvider>
  );
}
