import * as React from 'react';
import { styled } from '@mui/material/styles';
import Box from '@mui/material/Box';
import Grid from '@mui/material/Grid';
import Container from '@mui/material/Container';
import Paper from '@mui/material/Paper';
import Typography from '@mui/material/Typography';
import List from '@mui/material/List';
import ListItem from '@mui/material/ListItem';
import ListItemButton from '@mui/material/ListItemButton';
import ListItemIcon from '@mui/material/ListItemIcon';
import ListItemText from '@mui/material/ListItemText';
import Divider from '@mui/material/Divider';
import Rating from '@mui/material/Rating';

const config = require('../config.json');

export default function RecipePage({recipeId}) {

  const [expanded, setExpanded] = React.useState(false);
  const [recipeData, setRecipeData] = React.useState({});

  React.useEffect(() => {
  fetch(`http://${config.server_host}:${config.server_port}/recipe/${recipeId}`)
    .then(res => res.json())
    .then(resJson => setRecipeData(resJson))
}, [recipeId]);

  return (
      <Grid container spacing={2} display="flex" direction="row" alignItems="stretch">

        <Grid item xs={12}>
          <Paper sx={{ p: 2,}}>

            <Typography variant="h5" align="left" gutterBottom>
            {recipeData.name}
            </Typography>

            <Box sx={{width: 0, display: "flex", alignItems: "left"}}>
            <Rating name="read-only" value={4.5} readOnly align="left"/>
            <Box sx={{ ml: 2 }}>{4.5}</Box>
            </Box>

            <Typography variant="body2" align="left" gutterBottom>
            Submitted by {recipeData.user_id}
            </Typography> 

            <Typography variant="body1" align="left" gutterBottom>
            {recipeData.description}
            </Typography>

          </Paper>
        </Grid>


        <Grid item xs={4}>
          <Paper sx={{ p: 2,}}>

          </Paper>
        </Grid>


        <Grid item xs={4}>
          <Paper sx={{ p: 2,}}>
          <List>

          <ListItem>
              <ListItemText primary="Ingredients" secondary={recipeData.n_ingredients}/>
          </ListItem>

          <ListItem>
              <ListItemText primary="Minutes" secondary={recipeData.minute} />
          </ListItem>

          <ListItem>
              <ListItemText primary="Steps" secondary={recipeData.n_steps}/>
          </ListItem>
        </List>

          </Paper>
        </Grid>

      </Grid>
  );
}
